document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".nice-select").forEach(function(e){const t=e.querySelector(".current"),c=e.querySelectorAll(".option"),n=e.querySelector(".list");e.addEventListener("click",function(t){t.target.classList.contains("option")||e.classList.contains("open")?e.classList.remove("open"):(document.querySelectorAll(".nice-select").forEach(function(e){e.classList.remove("open")}),e.classList.add("open"))}),c.forEach(function(o){o.addEventListener("click",function(n){o.classList.contains("disabled")||(t.innerText=o.innerText,t.dataset.value=o.dataset.value,c.forEach(function(e){e.classList.remove("selected")}),o.classList.add("selected"),e.classList.remove("open"))})}),document.addEventListener("click",function(t){e.contains(t.target)||e.classList.remove("open")}),e.addEventListener("keydown",function(t){const c=n.querySelector(".focus")||n.querySelector(".selected");if("Enter"===t.key||" "===t.key)return e.classList.contains("open")?c.click():e.click(),t.preventDefault(),!1;if("ArrowDown"===t.key){if(e.classList.contains("open")){let t=c?c.nextElementSibling:n.firstElementChild;for(;t&&t.classList.contains("disabled");)t=t.nextElementSibling;t&&(c&&c.classList.remove("focus"),t.classList.add("focus"))}else e.click();return t.preventDefault(),!1}if("ArrowUp"===t.key){if(e.classList.contains("open")){let t=c?c.previousElementSibling:n.lastElementChild;for(;t&&t.classList.contains("disabled");)t=t.previousElementSibling;t&&(c&&c.classList.remove("focus"),t.classList.add("focus"))}else e.click();return t.preventDefault(),!1}"Escape"===t.key&&e.classList.remove("open")})})});
